@app.route("/lottery")
def lottery():
    url='https://www.dhlottery.co.kr/common.do?method=getLottoNumber&drwNo=866'
    scratch=requests.get(url).json()
    win=[]
    for i in range(6):
        win.append(scratch[f'drwtNo{i+1}'])
    sug=sorted(random.sample(range(1,46),6))

    pri=len(set(win)&set(sug))
    if pri==6:
        result='1등'
    elif pri>=3:
        result=f'{8-pri}등'
    else:
        result='꽝!'

    return render_template('lottery.html', sug=sug, result=result)

@app.route("/newyear")
def newyear():
    month = datetime.now().month
    day = datetime.now().day
    today = datetime.now()
    if month == 1 and day == 1:
        return "<h1>yeppp</h1>" + str(today)
    else:
        return "<h1>Noppp</h1>" + str(today)

